imgl=eval(result.match(/var chapterImages = (\[[\s\S]+?\])/)[1]);
host=result.match(/var nodeHost = "([^"]*)";/)[1].replace(/\\/g,'');
Cih='https://res.zuimh.com';
Cirh='https://respc.zuimh.com';
res0826='https://res0826.zuimh.com';
html='';
function get7ImageUrl(image) {
    if (image.match(/^(\/r\/?)/i)) {
        return res0826 + image
    } else if (image.match(/^(\/|images?)/i)) {
        return Cih + image
    }
}

for(i in imgl){
if(!host){
url=get7ImageUrl(imgl[i]);
}else{
url=host+imgl[i]
}
html+='<img src="'+url+'">\n'
}
html

