@js:
if(result.match(/目录/)){
list=result.match(/\{"title":"([^"]*)","href":"([^"]*)"\}/g);
html=[];
for(i=0;i<list.length;i++){
json=list[i].match(/\{"title":"([^"]*)","href":"([^"]*)"\}/);
html.push({
title:unescape(json[1].replace(/\\/g,'%')),
href:json[2].replace(/$/g,',{"webView":true}')
})
}
}else{

list=result.match(/\{"title":"([^"]+)".*?"span":"([^"]+)","href":"([^"]+)".*?\}/g);
html=[];
for(i in list){
json=list[i].match(/\{"title":"([^"]+)".*?"span":"([^"]+)","href":"([^"]+)".*?\}/);
html.push({
title:unescape(json[2].replace(/\\/g,'%')),
href:json[3].replace(/^/g,'http://new-qk.lifves.com/'),
chapter:unescape(json[1].replace(/\\/g,'%'))
})
}
}
html

