var siteid=result.match(/siteid="([^"]*)"/)[1];
var id=result.match(/id="([^"]*)"/)[1];
var oid=result.match(/oid="([^"]*)"/)[1];
var headers={
          Referer:baseUrl,
          "X-Requested-With":"XMLHttpRequest"
}
var options={headers:JSON.stringify(headers)}
var url='http://www.ting74.com/novelsearch/chapter/mp3.html?siteid='+siteid+'&&id='+id+'&&oid='+oid+',';

result=JSON.parse(java.ajax(url+JSON.stringify(options))).info.match(/src":"([^"]*)/);
result=(result)?result[1]:'';
eval('"'+ result +'"')

