function U(e) {
    return e.split("").map( e => {
        return e.match(/[A-Za-z]/) ? (c = Math.floor(e.charCodeAt(0) / 97), k = (e.toLowerCase().charCodeAt(0) - 83) % 26 || 26, String.fromCharCode(k + (0 == c ? 64 : 96))) : e
   }).join("")
}
result && result.length > 10 ? java.base64Decode(U(result)) : '这是 🔒 付费章节 哦！\n';

ruleSearch.bookList:
@js:
JSON.parse(result).data.slice(0, 10).map(o =>{
    var bid = o.bid;
    var chapterUrl = 'http://t.shuqi.com/#!/ct/cover/bid/' + bid + '/';
    var html = String(java.ajax(chapterUrl));
    var userId = '8000000';
    var encryptKey = html.match(/\sencryptKey:\s*'(\w+)'/)[1];
    var timestamp = Date.now();
    var sign = java.md5Encode(String(bid) + timestamp + userId + encryptKey);
    var params ='bookId=' + bid + '&user_id=' + userId + '&sign=' + sign + '&timestamp=' + timestamp +  '&shuqi_h5=';
    var option = JSON.stringify({"method": "POST","body": String(params)});
    var infoUrl = 'http://walden1.shuqireader.com/webapi/book/info,'+ option;
    var J = JSON.parse(java.ajax(infoUrl)).data;
    return {
        name: o.title,
        author: o.author,
        tags: J.className + ','+ (J.state == '2' ? '完结': '连载'),
        num: J.wordCount + '万字',
        latest: String(J.lastChapter.chapterName).replace(/\s+/, ' ').trim() + '（' + new Date(Number(J.lastChapter.updateTime) * 1000).toISOString().replace(/T.+/, '') + '）',
        cover: J.imgUrl || 'http://uee.me/bNkEy',
        intro: String(J.desc).replace(/[\s\t]+/g, ''),
        catalogUrl: 'http://content.shuqireader.com/webapi/book/chapterlist,'+ option
    }
}).filter(v =>v.name);

