.book-list li@js:
result.toArray().map(o =>{
    var detail= String(o.select('.book-name').attr('href')).replace(/^\//, baseUrl.match(/(.+\/\/.+?\/)/)[1]);
    var oText = s=>String(o.select(s).text().trim());
    var J = org.jsoup.Jsoup.parse(java.ajax(detail));
    var jText = s=>String(J.select(s).text().trim());
    return {
        name: oText('.book-name'),
        author: oText('.smaller').replace(/\s*作者：(\S+).+/, '$1'),
        tags: [String(J.select('.book-title .tag').toArray().map(tag =>tag.text()).join(' / '),J.select('.book-info > .row .tag').toArray().map(tag =>String(tag.text())).filter(v =>v.length < 5).slice(0, 2).join(','))].join(','),
        num:jText('.book-info-numbers span:eq(0)').replace(/●|总字数：/g, '')+'字',
        latest: oText('.smaller').replace(/.+\D(\d+-\d+-\d+).+\/\s*(.+)/, '$2（$1）').replace(/[\(（【].*[求更谢乐发推].*[】）\)]/,'').replace(/.*(第[^章]+章)/,'$1'),
        intro: jText('.book-brief').replace(/\s*简介：/, '').replace(/[\s\t]+/g, ''),
        cover: o.select('.cover').attr('data-original'),
        catalogUrl: detail
    }
}).filter(v =>v.name);

ruleToc.chapterList:
$.data.list
<js>
//二级目录，Json
var sid = java.get('sid');
var bid = java.get('bid');
var jdoc = JSON.parse(result);
var chUrl = 'https://contentxs.pigqq.com/BookFiles/Html/'+sid+'/'+bid+'/';
var volUrl = 'https://translate.google.cn/#view=home&op=translate&sl=zh-CN&tl=en&text=';
var list = [];
//显示卷名
var opts = {
  showVolName: false
};

sm=java.get('sm');

function format(title){
  return title.trim().replace(/^(\d+)(?![\s\d卷部章节回.])/, '$1 ').replace(/\s+/g, "\x20\x20");
}

jdoc.forEach((vol,idx) => {
  var chs = vol.list.map(ch => ({
    text: !vol.name?format(ch.name):((opts.showVolName ? '🍈[' + format(vol.name) + ']🍈' : (ch.IsVip === '1' ? '💰' : '').padStart(3, "\u2000")) + format(ch.name)),
    href: chUrl+ch.id+'.html',
    name: format(ch.name)
  }));
if(vol.name!=""){
  var volInfo = sm+'\\'+ format(vol.name);

  list.push({
    text: '♦️[' + format(vol.name) + ']♦️',
    href: volUrl + encodeURIComponent(volInfo)
});
}
  list = list.concat(chs);
});
result = list;
</js>

